function toggleClass(e,t){if(e&&t){var n=e.className,s=n.indexOf(t);-1===s?n+=" "+t:n=n.substr(0,s)+n.substr(s+t.length),e.className=n}}document.getElementById("mobile-menu").addEventListener("click",function(e){e.preventDefault(),toggleClass(document.getElementById("desktop-menu"),"active")}),document.getElementById("expand-top-songs").addEventListener("click",function(e){e.preventDefault(),toggleClass(document.getElementById("top-songs-panel"),"active"),document.getElementById("chevron-icon").className="",document.getElementById("top-songs-panel").classList.contains("active")?toggleClass(document.getElementById("chevron-icon"),"chevron-up"):toggleClass(document.getElementById("chevron-icon"),"chevron-down")});